var raspi = require('raspi');
var RotaryEncoder = require('./lib/').RotaryEncoder;

raspi.init(function() {
  var encoder = new RotaryEncoder({
    pins: { a: 5, b: 4 },
    pullResistors: { a: "up", b: "up" }
  });

  encoder.addListener('change', function (evt) {
    console.log('Count', evt.count);
  })
});
